package org.matheclipse.core.interfaces;

import com.duy.lambda.IntFunction;

/**
 * Created by Duy on 2/20/2018.
 */

public abstract class IASTMutableImpl extends IASTImpl implements IASTMutable {
    /**
     * Set all elements generated by the given function from index <code>start</code> inclusive to <code>end</code>
     * exclusive.
     *
     * @param start    start index (inclusive)
     * @param end      end index (exclusive)
     * @param function function which generates the elements which should be set
     * @return <tt>this</tt>
     */
    public IASTMutable setArgs(int start, int end, IntFunction<IExpr> function) {
        for (int i = start; i < end; i++) {
            set(i, function.apply(i));
        }
        return this;
    }

    /**
     * Set all elements generated by the given function from index <code>1</code> inclusive to <code>end</code>
     * exclusive.
     *
     * @param end      end index (exclusive)
     * @param function function which generates the elements which should be set
     * @return <tt>this</tt>
     */
    public IASTMutable setArgs(int end, IntFunction<IExpr> function) {
        return setArgs(1, end, function);
    }
}
