  @Test
  public void testFactorial() {
    check("Factorial(5.211111111111111111)", //
        "172.7183563387097672");
    check("(5/2)!", //
        "15/8*Sqrt(Pi)");
    check("(-7/2)!", //
        "-8/15*Sqrt(Pi)");
    check("(-121/2)!", //
        "1152921504606846976/6972993461801137628817411854132406856519268086997480719774989309037307630499025\\\n"
            + "82163482720947265625*Sqrt(Pi)");
    check("Factorial(I*Infinity)", //
        "0");
    check("Factorial(-I*Infinity)", //
        "0");
    check("Factorial(ComplexInfinity)", //
        "Indeterminate");
    check("Factorial(Factorial(x))", //
        "(x!)!");
    check("Factorial(2.5)", //
        "3.32335");
    check("Factorial(Infinity)", //
        "Infinity");

    check("Factorial(-1/2)", //
        "Sqrt(Pi)");
    check("Factorial(1/2)", //
        "Sqrt(Pi)/2");
    check("Factorial(0)", //
        "1");
    check("Factorial(1)", //
        "1");
    check("Factorial(-1)", //
        "ComplexInfinity");
    check("Factorial(10)", //
        "3628800");
    check("Factorial(-10)", //
        "ComplexInfinity");
    check("Factorial(11)", //
        "39916800");
    check("Factorial(-11)", //
        "ComplexInfinity");
    check("Factorial(19)", //
        "121645100408832000");
    check("Factorial(20)", //
        "2432902008176640000");
    check("Factorial(21)", //
        "51090942171709440000");
    checkNumeric("10.5!", //
        "1.1899423083962249E7");
    check("!a! //FullForm", //
        "Not(Factorial(a))");
  }

